package Strings;

public class Concat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "Pooja";  
        String str2 = "shree";  
        String str3 = "bs";  
         
        String str4 = str1.concat(str2);          
        System.out.println(str4);  
        
        String str5 = str1.concat(str2).concat(str3);  
        System.out.println(str5);  

	}

}
